mapper.select:
select

from z_contracts


        and chain_code = #{chainCode}


        and status = #{status}


        and DATE_FORMAT(start_date,'%Y-%m-%d')  >=  DATE_FORMAT(#{startDate},'%Y-%m-%d')


        and DATE_FORMAT(#{endDate},'%Y-%m-%d')  >=  DATE_FORMAT(end_date,'%Y-%m-%d')


        and create_code = #{userCode}


        and chain_code in

            #{chainCodeList}


    and contract_type='10'
    order by id desc

select

from z_contracts


        and chain_code = #{chainCode}


        and status = #{status}


        and DATE_FORMAT(start_date,'%Y-%m-%d')  >=  DATE_FORMAT(#{startDate},'%Y-%m-%d')


        and DATE_FORMAT(#{endDate},'%Y-%m-%d')  >=  DATE_FORMAT(end_date,'%Y-%m-%d')

    and create_code = #{userCode}
    and status = '1'
    and contract_type in ('20','30')
    order by id desc

select

from z_contracts base


        and chain_code = #{chainCode}


        and DATE_FORMAT(start_date,'%Y-%m-%d')  >=  DATE_FORMAT(#{startDate},'%Y-%m-%d')


        and DATE_FORMAT(#{endDate},'%Y-%m-%d')  >=  DATE_FORMAT(end_date,'%Y-%m-%d')


        and chain_code in

            #{chainCodeList}


    and not EXISTS (select 1 from z_contracts where
        parent_id=base.id and contract_type in ('20','30') and status = '1' and org_code = #{littleXwCode}
        )
    and contract_type='10' and status = '0'
    order by id desc

select

from z_contracts
where create_code =#{userCode}
and group_id = #{groupId}
and parent_id != 0

select * from z_contracts base where contract_type='10' and status='0'

    and DATE_FORMAT(start_date,'%Y-%m-%d')  >=  DATE_FORMAT(#{startDate},'%Y-%m-%d')


    and DATE_FORMAT(#{endDate},'%Y-%m-%d')  >=  DATE_FORMAT(end_date,'%Y-%m-%d')


    and chain_code in

        #{item}


and not EXISTS (select 1 from z_contracts where
parent_id=base.id and contract_type='20' and org_code=#{loginBuCode}
)

select

from z_contracts
where join_time  >=  #{startTime}
and join_time  <=  #{endTime}
and group_id = #{groupId}
and parent_id = 0

select

from (  select *
        from z_contracts
        where create_code = #{userCode}
        and status = '1'
        and contract_type in ('20','30')
        and DATE_FORMAT(create_time,'%Y%m')=#{month}
     UNION all (
        select *
        from z_contracts c
        where create_code = #{userCode}
        and contract_type='10'
        and DATE_FORMAT(create_time,'%Y%m')=#{month}
        and not EXISTS (
            select 1
            from z_contracts
            where create_code = #{userCode}
            and status = '1'
            and contract_type in ('20','30')
            and parent_id = c.id
        )
)) a
ORDER BY a.create_time DESC
