mapper.select:
select
  id, chain_code, chain_name, ty_master_xw_code, ty_master_xw_name, ty_master_empsn, ty_master_empname, ty_region_code, cd_master_xw_code, cd_master_xw_name, cd_master_empsn, cd_master_empname, contracts_id, create_date, ty_chain_percents, cd_chain_percents, market_percents, net_percents, plan_setps, share_setps, chain_pt_code, deleted
from chain_contract.z_hr_chain_info


        and id = #{id}


        and chain_code = #{chainCode}


        and chain_name = #{chainName}


        and ty_master_xw_code = #{tyMasterXwCode}


        and ty_master_xw_name = #{tyMasterXwName}


        and ty_master_empsn = #{tyMasterEmpsn}


        and ty_master_empname = #{tyMasterEmpname}


        and ty_region_code = #{tyRegionCode}


        and cd_master_xw_code = #{cdMasterXwCode}


        and cd_master_xw_name = #{cdMasterXwName}


        and cd_master_empsn = #{cdMasterEmpsn}


        and cd_master_empname = #{cdMasterEmpname}


        and contracts_id = #{contractsId}


        and create_date = #{createDate}


        and ty_chain_percents = #{tyChainPercents}


        and cd_chain_percents = #{cdChainPercents}


        and market_percents = #{marketPercents}


        and net_percents = #{netPercents}


        and plan_setps = #{planSetps}


        and share_setps = #{shareSetps}


        and chain_pt_code = #{chainPtCode}


        and deleted = #{deleted}
