mapper.select:
select
  id, lq_code, lq_name, node_code, node_name, xw_code, xw_name, target_code, target_name, target_line, share_percent,xwType3Code,xwType3
from chain_contract.z_node_target_percent_info


        and id = #{id}


        and lq_code = #{lqCode}


        and lq_name = #{lqName}


        and node_code = #{nodeCode}


        and node_name = #{nodeName}


        and xw_code = #{xwCode}


        and xw_name = #{xwName}


        and target_code = #{targetCode}


        and target_name = #{targetName}


        and target_line = #{targetLine}


        and parent_chain_code = #{parentChainCode}


        and xwType3Code = #{xwType3Code}


        and xwType3 = #{xwType3}

        and share_percent is not null

SELECT tb.`target_code`, tb.`target_name`, tb.`target_unit`, tb.target_to, tb.`target_bottom_line`, tpi.`share_percent` FROM z_node_target_percent_info tpi JOIN z_target_basic tb ON tpi.`node_code` = tb.`role_code` AND tpi.`target_code` = tb.`target_code` WHERE tpi.`node_code` = #{nodeCode}
AND tb.period_code in
#{yearMonth}
AND tb.target_pt_code = #{ptCode} AND tpi.lq_code = #{chainCode}

update.foreach:
update z_node_target_percent_info
set share_percent = #{item.sharePercent}
where id = #{item.id}
